$(function(){
		$(`#id`).css(`border`, `1px solid #00f`);
		$(`#pwd`).css(`border`, `1px solid #f00`);
		
		$(`.row`).css(`border`, `1px dotted #0f0`)
				.css(`background`, `#aaf`)
				.css(`color`, `#fff`);
		$(`table`).css(`border`, `2px solid #00f`)
				.css(`border-collapse`, `collapse`);
		$(`input[type=submit]`).css(`border`, `1px solid #000`);
	});